import React, { useState, useEffect, useRef } from 'react';

const hazards = ["„Éï„Ç©„Éº„ÇØ„É™„Éï„Éà", "„Ç≥„É≥„Éô„É§„Éº", "„Éó„É¨„ÇπÊ©ü", "Ë∂≥Â†¥", "„Éú„Ç§„É©„Éº", "ÊóãÁõ§", "„ÉÅ„Çß„Éº„É≥„ÇΩ„Éº", "„Éñ„É´„Éâ„Éº„Ç∂„Éº", "„ÇØ„É¨„Éº„É≥Ëªä", "ÂúßÂäõÂÆπÂô®", "ÈõªÊ∞óË®≠ÂÇô", "ÈöéÊÆµ„Éª„ÅØ„Åó„ÅîÈÅì", "ÊîØ‰øùÂ∑•", "‰ΩúÊ•≠Â∫ä", "Ê∫ù„Éª„Éî„ÉÉ„Éà", "ÂºïÁÅ´ÊÄß„ÅÆÁâ©", "ÂèØÁáÉÊÄß„ÅÆ„Ç¨„Çπ", "ÊúâÂÆ≥Áâ©Ë≥™", "ÊîæÂ∞ÑÁ∑ö", "Áí∞Â¢ÉË¶ÅÂõ†(Âú∞Èù¢„ÉªÂ∫äÈù¢)"];
const risks = ["Â¢úËêΩ„ÉªËª¢ËêΩ", "„ÅØ„Åï„Åæ„Çå„ÉªÂ∑ª„ÅçËæº„Åæ„Çå", "ÊøÄÁ™Å„Åï„Çå", "È£õÊù•„ÉªËêΩ‰∏ã", "Â¥©Â£ä„ÉªÂÄíÂ£ä", "Ëª¢ÂÄí", "Âàá„Çå„Éª„Åì„Åô„Çå", "Ë∏è„ÅøÊäú„Åç", "„Åä„Åº„Çå", "ÊÑüÈõª", "ÁÅ´ÁÅΩ", "ÁàÜÁô∫", "Á†¥Ë£Ç", "È´òÊ∏©„Éª‰ΩéÊ∏©„Å®„ÅÆÊé•Ëß¶", "ÊúâÂÆ≥Áâ©Á≠â„Å®„ÅÆÊé•Ëß¶", "‰∫§ÈÄö‰∫ãÊïÖÔºàÈÅìË∑ØÔºâ", "‰∫§ÈÄö‰∫ãÊïÖÔºà„Åù„ÅÆ‰ªñÔºâ", "Âãï‰Ωú„ÅÆÂèçÂãï„ÉªÁÑ°ÁêÜ„Å™Âãï‰Ωú", "„Åù„ÅÆ‰ªñ", "Êú™ÂàÜÈ°û"];

export default function InputPanel() {
  const [hazard, setHazard] = useState('');
  const [risk, setRisk] = useState('');
  const [report, setReport] = useState('');
  const [detailedReport, setDetailedReport] = useState('');
  const [transcriptText, setTranscriptText] = useState('');
  const recognitionRef = useRef(null);

  const handleSubmit = async () => {
    const response = await fetch('http://localhost:5000/api/report', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ hazard, risk })
    });
    const data = await response.json();
    setReport(data.result);
    setDetailedReport('');
  };

  const handleDetailedReport = async () => {
    const prompt = `„ÅÇ„Å™„Åü„ÅØÊó•Êú¨„ÅÆÂä¥ÂÉçÂÆâÂÖ®Ë°õÁîü„ÅÆÂ∞ÇÈñÄÂÆ∂„Åß„Åô„ÄÇ„Äå${hazard}„Äç„Å®„ÅÑ„ÅÜ‰ΩúÊ•≠Áí∞Â¢É„Å´„Åä„ÅÑ„Å¶„Äå${risk}„Äç„ÅÆÂç±Èô∫„Åå„ÅÇ„Çã„Å®‰ªÆÂÆö„Åó„ÄÅ„Åì„Çå„Å´ÂØæ„Åô„ÇãÊîπÂñÑÊèêÊ°à„Çí500ÊñáÂ≠óÁ®ãÂ∫¶„ÅßÂÖ∑‰ΩìÁöÑ„Åã„Å§ÂÆüÁî®ÁöÑ„Å´Ë©≥„Åó„ÅèËø∞„Åπ„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ„Éï„Ç©„Éº„Éû„É´„Å™Ë©±„ÅóË®ÄËëâ„Åß„ÄÅË™ûÂ∞æ„ÅØ„Äå„Äú„Çí„Åä„Åô„Åô„ÇÅ„Åó„Åæ„Åô„Äç„Äå„Äú„ÅåÊúõ„Åæ„Çå„Åæ„Åô„Äç„Å™„Å©„ÅßÁ∑†„ÇÅ„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ‰ª•‰∏ã„ÅÆ5„Å§„ÅÆ„Ç´„ÉÜ„Ç¥„É™„Å´ÂàÜÈ°û„Åó„Å¶Âá∫Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑÔºöÔºà0ÔºâÊ≥ïÁöÑË¶ÅÊ±Ç‰∫ãÈ†ÖÔºà1ÔºâÊú¨Ë≥™ÁöÑÂØæÁ≠ñÔºà2ÔºâÂ∑•Â≠¶ÁöÑÂØæÁ≠ñÔºà3ÔºâÁÆ°ÁêÜÁöÑÂØæÁ≠ñÔºà4Ôºâ‰øùË≠∑ÂÖ∑„ÅÆ‰ΩøÁî®„ÄÇË©≤ÂΩì„Åå„Å™„ÅÑÂ†¥Âêà„ÅØ„Äå„Å™„Åó„Äç„Å®„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ`;
    const response = await fetch('http://localhost:5000/api/report', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ hazard, risk, prompt })
    });
    const data = await response.json();
    setDetailedReport(data.result);
  };

  useEffect(() => {
    if (!('webkitSpeechRecognition' in window)) return;
    const recognition = new window.webkitSpeechRecognition();
    recognition.lang = 'ja-JP';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;
    recognitionRef.current = recognition;
  }, []);

  const handleVoiceInput = (type) => {
    const recognition = recognitionRef.current;
    if (!recognition) return;
    try {
      recognition.start();
    } catch (error) {
      if (error.name === 'InvalidStateError') {
        recognition.stop();
        recognition.start();
      } else {
        console.error("Èü≥Â£∞Ë™çË≠ò„Ç®„É©„Éº:", error);
      }
    }
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setTranscriptText(`Ë™çË≠òÁµêÊûúÔºà${type === 'hazard' ? 'Ê¥ó„ÅÑÂá∫„Åó' : 'Âç±Èô∫Áä∂Ê≥Å'}ÔºâÔºö${transcript}`);
      if (type === 'hazard') setHazard(transcript);
      if (type === 'risk') setRisk(transcript);
    };
  };

  return (
    <div>
      <h2>Âä¥ÁÅΩ„É™„Çπ„ÇØÂ†±ÂëäÊõ∏„ÉÑ„Éº„É´ÔºàUTF-8ÁâàÔºâ</h2>
      <label>Ê¥ó„ÅÑÂá∫„ÅóÂÜÖÂÆπÔºö</label><br />
      <input type="text" value={hazard} onChange={e => setHazard(e.target.value)} placeholder="Áõ¥Êé•ÂÖ•Âäõ„Åæ„Åü„ÅØÈÅ∏Êäû" />
      <select value={hazard} onChange={e => setHazard(e.target.value)}>
        <option value="">ÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ</option>
        {hazards.map(h => <option key={h} value={h}>{h}</option>)}
      </select>
      <button onClick={() => handleVoiceInput('hazard')}>üé§ Ë©±„Åô</button>
      <br /><br />

      <label>Âç±Èô∫Áä∂Ê≥ÅÔºö</label><br />
      <input type="text" value={risk} onChange={e => setRisk(e.target.value)} placeholder="Áõ¥Êé•ÂÖ•Âäõ„Åæ„Åü„ÅØÈÅ∏Êäû" />
      <select value={risk} onChange={e => setRisk(e.target.value)}>
        <option value="">ÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ</option>
        {risks.map(r => <option key={r} value={r}>{r}</option>)}
      </select>
      <button onClick={() => handleVoiceInput('risk')}>üé§ Ë©±„Åô</button>
      <br /><br />

