import React, { useState, useEffect, useRef } from 'react';

export default function InputPanel() {
  const [hazard, setHazard] = useState('');
  const [risk, setRisk] = useState('');
  const [report, setReport] = useState('');
  const [detailedReport, setDetailedReport] = useState('');
  const [transcriptText, setTranscriptText] = useState('');
  const recognitionRef = useRef(null);

  const handleSubmit = async () => {
    const response = await fetch('http://localhost:5000/api/report', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ hazard, risk })
    });
    const data = await response.json();
    setReport(data.result);
    setDetailedReport('');
  };

  const handleDetailedReport = async () => {
    const prompt = `„ÅÇ„Å™„Åü„ÅØÊó•Êú¨„ÅÆÂä¥ÂÉçÂÆâÂÖ®Ë°õÁîü„ÅÆÂ∞ÇÈñÄÂÆ∂„Åß„Åô„ÄÇ„Äå${hazard}„Äç„Å®„ÅÑ„ÅÜ‰ΩúÊ•≠Áí∞Â¢É„Å´„Åä„ÅÑ„Å¶„Äå${risk}„Äç„ÅÆÂç±Èô∫„Åå„ÅÇ„Çã„Å®‰ªÆÂÆö„Åó„ÄÅ„Åì„Çå„Å´ÂØæ„Åô„ÇãÊîπÂñÑÊèêÊ°à„Çí500ÊñáÂ≠óÁ®ãÂ∫¶„ÅßÂÖ∑‰ΩìÁöÑ„Åã„Å§ÂÆüÁî®ÁöÑ„Å´Ë©≥„Åó„ÅèËø∞„Åπ„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ„Éï„Ç©„Éº„Éû„É´„Å™Ë©±„ÅóË®ÄËëâ„Åß„ÄÅË™ûÂ∞æ„ÅØ„Äå„Äú„Çí„Åä„Åô„Åô„ÇÅ„Åó„Åæ„Åô„Äç„Äå„Äú„ÅåÊúõ„Åæ„Çå„Åæ„Åô„Äç„Å™„Å©„ÅßÁ∑†„ÇÅ„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ„Åæ„Åü„ÄÅ‰ª•‰∏ã„ÅÆ5„Å§„ÅÆ„Ç´„ÉÜ„Ç¥„É™„Å´ÂàÜÈ°û„Åó„Å¶Âá∫Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑÔºöÔºà0ÔºâÊ≥ïÁöÑË¶ÅÊ±Ç‰∫ãÈ†ÖÔºà1ÔºâÊú¨Ë≥™ÁöÑÂØæÁ≠ñÔºà2ÔºâÂ∑•Â≠¶ÁöÑÂØæÁ≠ñÔºà3ÔºâÁÆ°ÁêÜÁöÑÂØæÁ≠ñÔºà4Ôºâ‰øùË≠∑ÂÖ∑„ÅÆ‰ΩøÁî®„ÄÇË©≤ÂΩì„Åå„Å™„ÅÑÂ†¥Âêà„ÅØ„Äå„Å™„Åó„Äç„Å®„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ`;
    const response = await fetch('http://localhost:5000/api/report', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ hazard, risk, prompt })
    });
    const data = await response.json();
    setDetailedReport(data.result);
  };

  useEffect(() => {
    if (!('webkitSpeechRecognition' in window)) return;
    const recognition = new window.webkitSpeechRecognition();
    recognition.lang = 'ja-JP';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;
    recognitionRef.current = recognition;
  }, []);

  const handleVoiceInput = (type) => {
    const recognition = recognitionRef.current;
    if (!recognition) return;
    try {
      recognition.start();
    } catch (error) {
      if (error.name === 'InvalidStateError') {
        recognition.stop();
        recognition.start();
      } else {
        console.error("Èü≥Â£∞Ë™çË≠ò„Ç®„É©„Éº:", error);
      }
    }
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setTranscriptText(`Ë™çË≠òÁµêÊûúÔºà${type === 'hazard' ? 'Ê¥ó„ÅÑÂá∫„Åó' : 'Âç±Èô∫Áä∂Ê≥Å'}ÔºâÔºö${transcript}`);
      if (type === 'hazard') setHazard(transcript);
      if (type === 'risk') setRisk(transcript);
    };
  };

  return (
    <div>
      <h2>Âä¥ÁÅΩ„É™„Çπ„ÇØÂ†±ÂëäÊõ∏„ÉÑ„Éº„É´</h2>
      <label>Ê¥ó„ÅÑÂá∫„ÅóÂÜÖÂÆπÔºö</label><br />
      <input type="text" value={hazard} onChange={e => setHazard(e.target.value)} placeholder="Áõ¥Êé•ÂÖ•Âäõ" />
      <button onClick={() => handleVoiceInput('hazard')}>üé§ Ë©±„Åô</button>
      <br /><br />

      <label>Âç±Èô∫Áä∂Ê≥ÅÔºö</label><br />
      <input type="text" value={risk} onChange={e => setRisk(e.target.value)} placeholder="Áõ¥Êé•ÂÖ•Âäõ" />
      <button onClick={() => handleVoiceInput('risk')}>üé§ Ë©±„Åô</button>
      <br /><br />

      <button onClick={handleSubmit}>Â†±ÂëäÊõ∏„Çí‰ΩúÊàê„Åô„Çã</button>
      {report && (
        <>
          <pre style={{ whiteSpace: 'pre-wrap' }}>{report}</pre>
          <button onClick={handleDetailedReport}>‚ë£ ÊîπÂñÑÊèêÊ°àÔºàË©≥Á¥∞ÁâàÔºâ</button>
        </>
      )}
      {detailedReport && <pre style={{ whiteSpace: 'pre-wrap', color: 'darkblue' }}>{detailedReport}</pre>}
      {transcriptText && <p>{transcriptText}</p>}
    </div>
  );
}
